/// <reference path="../../scripts/typings/signalr/signalr.d.ts" />
/// <reference path="../../scripts/typings/angularjs/angular.d.ts" />
declare var _app: {
    roomNumber: number;
    controllerUrl: string;
    twitterHashtagUrl: string;
    apiBaseUrl: string;
};

module b2 {
    export import Vec2 = Box2D.Common.Math.b2Vec2;
    export import BodyDef = Box2D.Dynamics.b2BodyDef;
    export import Body = Box2D.Dynamics.b2Body;
    export import FixtureDef = Box2D.Dynamics.b2FixtureDef;
    export import Fixture = Box2D.Dynamics.b2Fixture;
    export import World = Box2D.Dynamics.b2World;
    export import PolygonShape = Box2D.Collision.Shapes.b2PolygonShape;
    export import CircleShape = Box2D.Collision.Shapes.b2CircleShape;
    export import DebugDraw = Box2D.Dynamics.b2DebugDraw;
}

module NaaS.Box {
    const enum CoinType {
        Like,
        Dis
    }

    class CoinAsset {
        public image: HTMLImageElement;

        constructor(
            public imageUrl: string,
            public imageRadius: number,
            public seUrl: string) {
            this.image = this.resizeImage(imageUrl, 2 * imageRadius);
        }
        
        // 参考：http://elicon.blog57.fc2.com/blog-entry-109.html
        private resizeImage(src: string, new_size: number): HTMLImageElement {
            var image_data = new Image();

            var img0 = new Image();
            img0.onload = () => {
                var canvas0 = document.createElement('canvas');

                var width = img0.naturalWidth;
                var height = img0.naturalHeight;
                var chouhen = (width >= height) ? width : height;
                var canvaswidth = width;
                var canvasheight = height;
                if (chouhen >= new_size) {
                    canvaswidth = new_size / chouhen * width;
                    canvasheight = new_size / chouhen * height;
                }
                canvas0.width = canvaswidth;
                canvas0.height = canvasheight;

                var context = canvas0.getContext("2d");
                context.drawImage(img0, 0, 0, canvaswidth, canvasheight);
                image_data.src = canvas0.toDataURL("image/png");
            }
            img0.src = src;

            return image_data;
        }
    }

    var CoinAssets = [
        new CoinAsset('/Content/images/coin.png', 20, 'data:audio/mp3;base64,'),
        new CoinAsset('/Content/images/stone.png', 15, 'data:audio/mp3;base64,')
    ];

    interface ThrowingData {
        throwPoint: number;
        typeOfZeni: number;
    }

    interface IScope extends ng.IScope {
        title: string;
    }

    class RoomController {
        hub: HubProxy;
        context: CanvasRenderingContext2D;
        world: Box2D.Dynamics.b2World;

        private worldWidth: number;
        private worldHeight: number;
        private worldScale = 30.0;
        private frameRate = 60;
        private worker: Worker;

        constructor($scope: IScope) {

            var canvas = <HTMLCanvasElement>document.getElementById('canvas');
            this.context = canvas.getContext('2d');
            this.worldWidth = canvas.width;
            this.worldHeight = canvas.height;

            this.hub = $.connection.hub.createHubProxy('DefaultHub');
            this.hub.on('Throw', this.OnThrow.bind(this));

            $.connection.hub
                .start()
                .then(() => this.hub.invoke('EnterRoom', _app.roomNumber))
                .then(r => $scope.$apply(() => {
                $scope.title = (<any>r).title;
            }));

            this.initWorld();

            this.worker = new Worker('/Views/Default/BoxWorker.js');
            this.worker.addEventListener('message', this.OnWorkerMessage.bind(this));
        }

        private OnThrow(data: ThrowingData): void {
            this.worker.postMessage({ cmd: 'Enqueue', data });
        }

        private OnWorkerMessage(e: MessageEvent): void {
            switch (e.data.cmd) {
                case 'Interval':
                    this.stepWorld();
                    break;
                case 'Enqueue':
                    this.OnEnqueueThrowing(e.data.data);
                    break;
            }
        }

        private OnEnqueueThrowing(data: ThrowingData) {
            var coinAsset = CoinAssets[data.typeOfZeni];
            var circleRadius = coinAsset.imageRadius;
            this.createCircle(this.world,
                circleRadius + (0 | ((this.worldWidth - 2 * circleRadius) * data.throwPoint)),
                -circleRadius,
                circleRadius,
                coinAsset.image);
            (new Audio(coinAsset.seUrl)).play();
            this.worker.postMessage({ cmd: 'Start', fps: this.frameRate });
        }

        private getDebugDraw() {
            var debugDraw = new b2.DebugDraw();
            debugDraw.SetSprite(this.context);
            debugDraw.SetDrawScale(this.worldScale);
            debugDraw.SetFillAlpha(0.5);
            debugDraw.SetLineThickness(1.0);
            debugDraw.SetFlags(b2.DebugDraw.e_shapeBit);
            return debugDraw;
        }

        private initWorld() {
            this.world = new b2.World(
                new b2.Vec2(0, 50), // 重力方向
                true                 // Sleepの可否
                );
            this.createFixedBox(0, 0, 2.0, this.worldHeight);
            this.createFixedBox(0, this.worldHeight - 2, this.worldWidth, 2.0);
            this.createFixedBox(this.worldWidth - 2, 0, 2.0, this.worldHeight);

            this.world.SetDebugDraw(this.getDebugDraw());
        }

        private stepWorld() {
            this.world.Step(1 / this.frameRate, 10, 10);
            //this.world.DrawDebugData();
            this.world.ClearForces();
            var isAwake = this.render();
            if (isAwake == false) this.worker.postMessage({ cmd: 'Stop' });
        }

        private createFixedBox(x: number, y: number, width: number, height: number) {
            var bodyDef = new b2.BodyDef;
            bodyDef.type = b2.Body.b2_staticBody;

            // オブジェクトの設定
            var fixDef = new b2.FixtureDef;
            fixDef.density = 1.0;     // 密度
            fixDef.friction = 0.5;    // 摩擦係数
            fixDef.restitution = 0.4; // 反発係数

            // 床の設置
            fixDef.shape = new b2.PolygonShape;
            var shape: any = fixDef.shape;
            shape.SetAsBox(width / this.worldScale, height / this.worldScale);
            bodyDef.position.Set(x / this.worldScale, y / this.worldScale);
            this.world.CreateBody(bodyDef).CreateFixture(fixDef);
        }

        private createCircle(world: Box2D.Dynamics.b2World, x: number, y: number, r: number, img: HTMLImageElement) {
            var bodyDef = new b2.BodyDef;
            bodyDef.type = b2.Body.b2_dynamicBody;

            // オブジェクトの設定
            var fixDef = new b2.FixtureDef;
            fixDef.density = 100.0;     // 密度
            fixDef.friction = 0.5;     // 摩擦係数
            fixDef.restitution = 0.5;  // 反発係数
            fixDef.shape = new b2.CircleShape(r / this.worldScale);

            // 円形オブジェクトの設置
            bodyDef.position.x = x / this.worldScale;
            bodyDef.position.y = y / this.worldScale;
            bodyDef.userData = { img, r };
            world.CreateBody(bodyDef).CreateFixture(fixDef);
        }

        private render(): boolean {
            this.context.clearRect(0, 0, this.worldWidth, this.worldHeight);

            var isAwake = false;
            for (var bodyItem = this.world.GetBodyList(); bodyItem; bodyItem = bodyItem.GetNext()) {
                if (bodyItem.GetType() == b2.Body.b2_dynamicBody) {
                    isAwake = isAwake || bodyItem.IsAwake();
                    var pos = bodyItem.GetPosition();

                    this.context.save();
                    var userData = bodyItem.GetUserData();
                    if (userData && userData.img && userData.img.complete) {
                        var slideX = pos.x * this.worldScale;
                        var slideY = pos.y * this.worldScale;

                        this.context.translate(slideX, slideY);
                        this.context.rotate(bodyItem.GetAngle());
                        this.context.drawImage(userData.img, -userData.r, -userData.r);
                    }
                    this.context.restore();
                }
            }
            return isAwake;
        }
    }

    var theApp = angular.module('theApp', []);
    theApp.controller('roomController', RoomController);
}

module NaaS.Settings {
    interface IScope extends ng.IScope {
        twitterHashtag: string;
    }

    class SettingsController {
        constructor(private $scope: IScope, private $http: ng.IHttpService) {
        }

        public DoModal(): void {
            this.$http.get(_app.apiBaseUrl + '/Settings')
                .then(e => {
                this.$scope.twitterHashtag = (<IScope>e.data).twitterHashtag;
                $('#settings-dialog, .modal-mask')
                    .fadeIn('normal', _=> {
                    $('#settings-dialog *[autofocus]').focus();
                });
            });
        }

        public OK(): void {
            this.$http.put(_app.apiBaseUrl + '/Settings',
                $('#settings-dialog .form').serialize(),
                { headers: { 'content-type': 'application/x-www-form-urlencoded' } }
                ).then(_ => this.Close());
        }

        public Cancel(): void {
            this.Close();
        }

        private Close(): void {
            $('#settings-dialog, .modal-mask').fadeOut();
        }
    }

    var theApp = angular.module('theApp');
    theApp.controller('settingsController', SettingsController);

    $(() => {
        $('#lnk-settings').click(function (e) {
            e.preventDefault();
            angular
                .element(document.getElementById('settings-dialog'))
                .controller()
                .DoModal();
        });
    });
}